mapper.select:
select Member.gamesCategoryadmin_id,Member.member_id as admin_id,member_name,member_nick_name,member_gender,member_register_telphone,member_register_email,gamesCategorycreate_time,member_authority,member_status ,group_concat(MAuthority_Menus.Mau_id) Mau_id,group_concat(Mau_name) Mau_name from Member_Authority left join MAuthority_Menus on Member_Authority.Mau_id=MAuthority_Menus.Mau_id left join Member on Member_Authority.member_id=Member.member_id
gamesCategoryadmin_id=#{gamesCategoryadmin_id}
group by Member.member_id
